// Helper functions to extract specific information (same as before)
function extractProjectInfo(results) {
  const projectInfo = [];
  const projectNames = new Set();

  results.forEach(doc => {
    if (doc.content) {
      const content = doc.content.toLowerCase();

      // Extract project names
      const projectMatches = content.match(/(?:project|contract|job)[\s:]*([^\r\n]+)/gi);
      if (projectMatches) {
        projectMatches.forEach(match => {
          const cleaned = match.replace(/(?:project|contract|job)[\s:]*/gi, '').trim();
          if (cleaned.length > 10 && cleaned.length < 100) {
            projectNames.add(cleaned);
          }
        });
      }

      // Extract building information
      const buildingMatches = content.match(/building(?:s)?\s*(?:#\s*)?(\d+(?:\s*(?:to|[-â€“])\s*\d+)?)/gi);
      if (buildingMatches) {
        buildingMatches.forEach(match => {
          projectInfo.push(`- Buildings: ${match}`);
        });
      }
    }
  });

  // Add unique project names
  Array.from(projectNames).slice(0, 3).forEach(name => {
    projectInfo.push(`- Project: ${name}`);
  });

  return projectInfo;
}

function extractFinancialInfo(results) {
  const financialInfo = [];
  const amounts = [];

  results.forEach(doc => {
    if (doc.content) {
      const content = doc.content;

      // Extract dollar amounts
      const moneyMatches = content.match(/\$[\d,]+(?:\.\d{2})?/g);
      if (moneyMatches) {
        moneyMatches.forEach(amount => {
          const numericValue = parseInt(amount.replace(/[$,]/g, ''));
          if (numericValue >= 1000) { // Only significant amounts
            amounts.push({ amount, value: numericValue });
          }
        });
      }

      // Extract specific cost categories
      const costMatches = content.match(/(painting|resinous flooring|architectural)[^$]*\$[\d,]+/gi);
      if (costMatches) {
        costMatches.forEach(match => {
          financialInfo.push(`- ${match.trim()}`);
        });
      }
    }
  });

  // Add largest amounts found
  const sortedAmounts = amounts.sort((a, b) => b.value - a.value).slice(0, 5);
  sortedAmounts.forEach(item => {
    if (item.value >= 10000) { // Only show amounts $10k+
      financialInfo.push(`- Contract Value: ${item.amount}`);
    }
  });

  return [...new Set(financialInfo)]; // Remove duplicates
}

function extractSpecifications(results) {
  const specs = [];

  results.forEach(doc => {
    if (doc.content) {
      const content = doc.content;

      // Paint specifications
      const paintSpecs = content.match(/(?:sherwin williams?|benjamin moore|sw\s+\d+|color|epoxy paint)[^.\r\n]*(?:\.|$)/gi);
      if (paintSpecs) {
        paintSpecs.slice(0, 3).forEach(spec => {
          specs.push(`- Paint: ${spec.trim()}`);
        });
      }

      // Flooring specifications
      const flooringSpecs = content.match(/(?:ceramic carpet|resinous|epoxy flooring|gp\d+)[^.\r\n]*(?:\.|$)/gi);
      if (flooringSpecs) {
        flooringSpecs.slice(0, 3).forEach(spec => {
          specs.push(`- Flooring: ${spec.trim()}`);
        });
      }

      // Coverage specifications
      const coverageSpecs = content.match(/\d+\s*(?:sq\.?\s*ft\.?|sqft)/gi);
      if (coverageSpecs) {
        const uniqueCoverage = [...new Set(coverageSpecs)].slice(0, 3);
        uniqueCoverage.forEach(coverage => {
          specs.push(`- Coverage: ${coverage}`);
        });
      }
    }
  });

  return [...new Set(specs)].slice(0, 8); // Limit to 8 unique specs
}

function extractContactInfo(results) {
  let contactInfo = '';

  results.forEach(doc => {
    if (doc.content && !contactInfo) {
      const content = doc.content;

      // Extract company and contact information
      const companyMatch = content.match(/(?:dear|attn:|attention:)\s*([^,\r\n]+)/i);
      if (companyMatch) {
        contactInfo += `Contact: ${companyMatch[1].trim()}\n`;
      }

      // Extract phone numbers
      const phoneMatch = content.match(/(?:tel|phone)[:,\s]*(\(\d{3}\)\s*\d{3}-\d{4}|\d{3}[.-]\d{3}[.-]\d{4})/i);
      if (phoneMatch) {
        contactInfo += `Phone: ${phoneMatch[1]}\n`;
      }

      // Extract addresses
      const addressMatch = content.match(/\d+\s+[^,\r\n]+(?:street|st|avenue|ave|road|rd)[^,\r\n]*/i);
      if (addressMatch) {
        contactInfo += `Address: ${addressMatch[0]}\n`;
      }
    }
  });

  return contactInfo || null;
}

