if (deleteDocsData?.searchDeleteSkipped) {
  searchIndexStatus = 'skipped';
  searchDeletedCount = 0;
  searchMessage = 'No documents in search index to delete';
} else {
  // Azure Search returns the operation results in the value array
  // Each item in the array has a status field indicating success
  // A successful response has statusCode in the 200 range
  let statusCode = searchResponse.statusCode;

  // If statusCode is missing, check for the value array which indicates success
  if (!statusCode && searchResponse.value !== undefined) {
    // If we have a value array, the request was successful
    statusCode = 200;
  }

  // Also check for @odata.context which indicates a successful response
  if (!statusCode && searchResponse['@odata.context']) {
    statusCode = 200;
  }

  // Successful deletion
  if (statusCode === 200 || statusCode === 201 || statusCode === 202 || statusCode === 204) {
    searchIndexStatus = 'success';
    searchDeletedCount = deleteDocsData?.deletedCount || 0;
    searchMessage = `Successfully deleted ${searchDeletedCount} documents from search index`;
  } else if (statusCode) {
    // Failed with specific status code
    searchIndexStatus = 'failed';
    searchMessage = `Failed to delete from search index: HTTP ${statusCode}`;
  } else {
    // No status code available - check if we have any indication of success
    if (searchResponse.value && Array.isArray(searchResponse.value)) {
