const fileName = body.fileName || body.name || 'unknown.pdf';
const mimeType = body.mimeType || body.type || 'application/pdf';
const category = body.category || 'uncategorized';
const client = body.client || 'general';
const clientName = body.clientName || '';

// Check if file data exists
if (!fileData) {
    throw new Error('No file data provided in upload request');
}

// Calculate approximate file size from base64
// Base64 increases size by ~33%, so we reverse calculate
const base64Length = fileData.length;
const padding = (fileData.match(/=/g) || []).length;
const fileSize = Math.floor((base64Length * 3) / 4) - padding;

// Determine if file is large (> 10MB)
const isLargeFile = fileSize > 10 * 1024 * 1024;

console.log(`File: ${fileName}, Size: ${(fileSize / 1024).toFixed(2)} KB, Large: ${isLargeFile}`);

// Return data for next nodes
return {
    json: {
        file: fileData,
        fileName: fileName,
        mimeType: mimeType,
        category: category,
        client: client,
        clientName: clientName,
        fileSize: fileSize,
        isLargeFile: isLargeFile,
        uploadedAt: new Date().toISOString()
    }
};

// Optimized chunking with memory management
const content = $input.item.json.convertedContent || $input.item.json.content;
const CHUNK_SIZE = 1000; // Characters per chunk
const CHUNK_OVERLAP = 200; // Overlap between chunks

let chunks = [];
let text = '';

try {
  // Parse content if it's JSON
  if (typeof content === 'string') {
    try {
      const parsed = JSON.parse(content);
      text = parsed.content || parsed.text || JSON.stringify(parsed);
    } catch {
      text = content;
    }
  } else {
    text = JSON.stringify(content);
  }

  // Create overlapping chunks with memory efficiency
  for (let i = 0; i < text.length; i += (CHUNK_SIZE - CHUNK_OVERLAP)) {
    const chunk = text.slice(i, Math.min(i + CHUNK_SIZE, text.length));
    if (chunk.trim()) {
      chunks.push({
        id: `${$input.item.json.client}_${$input.item.json.fileName}_chunk_${chunks.length}`,
        content: chunk,
        index: chunks.length,
        client: $input.item.json.client,
        clientName: $input.item.json.clientName,
        category: $input.item.json.category,
        fileName: $input.item.json.fileName,
        chunkOf: $input.item.json.fileName,
        totalChunks: 0 // Will be updated
      });
    }

    // Free memory periodically
    if (chunks.length % 50 === 0) {
      global.gc && global.gc();
    }
  }

  // Update total chunks count
  chunks = chunks.map(chunk => ({
    ...chunk,
    totalChunks: chunks.length
  }));

} catch (error) {
  // Fallback for error cases
  chunks = [{
    id: `${$input.item.json.client}_${$input.item.json.fileName}_chunk_0`,
    content: 'Error processing document: ' + error.message,
    index: 0,
    client: $input.item.json.client,
    clientName: $input.item.json.clientName,
    category: $input.item.json.category,
    fileName: $input.item.json.fileName,
    error: error.message
  }];
}

return chunks.map(chunk => ({ json: chunk }));

