// Extract and prepare file data from webhook
const inputItem = $input.first();
const json = inputItem.json || {};

// Handle both direct JSON and nested body
const data = json.body || json;

// Extract file data and metadata
const fileData = data.file || data.fileBase64 || '';
const fileName = data.fileName || 'document.pdf';
const mimeType = data.mimeType || 'application/pdf';
const category = data.category || 'uncategorized';
const client = data.client || 'general';
const clientName = data.clientName || client;

if (!fileData) {
    throw new Error('No file data provided in upload request');
}

// Convert base64 to buffer
const buffer = Buffer.from(fileData, 'base64');
const fileSize = buffer.length;

// Generate safe file names and paths
const safeName = fileName.replace(/[^a-zA-Z0-9.-]/g, '_');
const timestamp = Date.now();

// Azure blob paths
const originalPath = `${client}/${category}/original/${safeName}`;
const convertedPath = `${client}/${category}/converted/${safeName.replace(/\.pdf$/i, '')}.jsonl`;

console.log(`Processing: ${fileName} (${(fileSize/1024).toFixed(2)}KB) for client: ${client}`);

return {
    json: {
        fileName: fileName,
        safeName: safeName,
        mimeType: mimeType,
        category: category,
        client: client,
        clientName: clientName,
        fileSize: fileSize,
        isLargeFile: fileSize > 10 * 1024 * 1024,
        originalPath: originalPath,
        convertedPath: convertedPath,
        uploadedAt: new Date().toISOString(),
        fileBase64: fileData,
        // Azure URLs
        originalBlobUrl: `https://saxtechfcs.blob.core.windows.net/fcs-clients/FCS-OriginalClients/${originalPath}?sp=racwdl&st=2025-08-08T05:00:57Z&se=2030-08-08T13:15:57Z&spr=https&sv=2024-11-04&sr=c&sig=lJKK9jDZ59pJSNkKSgwaQIrCdBaJzx4XPzgEB2%2FrnIg%3D`,
        convertedBlobUrl: `https://saxtechfcs.blob.core.windows.net/fcs-clients/FCS-OriginalClients/${convertedPath}?sp=racwdl&st=2025-08-08T05:00:57Z&se=2030-08-08T13:15:57Z&spr=https&sv=2024-11-04&sr=c&sig=lJKK9jDZ59pJSNkKSgwaQIrCdBaJzx4XPzgEB2%2FrnIg%3D`,
        // Function app URL
        functionAppUrl: `https://saxtech-docconverter.azurewebsites.net/api/ConvertDocument?code=KRitpiKC4_teemeHVrLWt8-vJdIvpSkzBFW0co3J4Q3FAzFuYbOMng==`,
        timestamp: timestamp
    },
    binary: {
        data: {
            data: buffer,
            mimeType: mimeType,
            fileName: fileName,
            fileExtension: fileName.split('.').pop() || 'pdf'
        }
    }
};

// Get data from previous nodes
const originalData = $node["Prepare File Data"].json;
const conversionResponse = $input.first().json;

// Extract the actual content from the conversion response
// The response might be nested in body or have success/data structure
let documentContent = "";
if (conversionResponse.body) {
  documentContent = conversionResponse.body.content || conversionResponse.body.text || JSON.stringify(conversionResponse.body);
} else if (conversionResponse.data) {
  documentContent = conversionResponse.data.content || conversionResponse.data.text || JSON.stringify(conversionResponse.data);
} else if (conversionResponse.content) {
  documentContent = conversionResponse.content;
} else if (conversionResponse.text) {
  documentContent = conversionResponse.text;
} else {
  // Remove success field if present and stringify the rest
  const {success, ...cleanData} = conversionResponse;
  documentContent = JSON.stringify(cleanData);
}

// Generate unique document ID
const docId = `${originalData.client}_${originalData.category}_${originalData.fileName}`
  .replace(/[^a-zA-Z0-9]/g, '_')
  .toLowerCase();

// Create the index document with ONLY the fields that exist in the schema
const indexDocument = {
  "@search.action": "upload",
  "id": docId,
  "content": documentContent,
  "client": originalData.client,
  "category": originalData.category,
  "fileName": originalData.fileName,
  "uploadedAt": originalData.uploadedAt || new Date().toISOString(),
  "blobPath": originalData.originalPath,
  "mimeType": originalData.mimeType,
  "convertedPath": originalData.convertedPath,
  "metadata": JSON.stringify({
    fileSize: originalData.fileSize,
    timestamp: originalData.timestamp
  })
  // contentVector will be populated by embeddings if you add that later
};

// Return the properly formatted payload for Azure Search
return {
  json: {
    value: [indexDocument]
  }
};

// Get data from previous nodes - handle both possible sources
const originalData = $node["Prepare File Data"] ? $node["Prepare File Data"].json : {};
const item = $input.first();
const json = item.json;

// Merge all available data
const allData = { ...originalData, ...json };

// Create JSONL format - each line is a complete JSON object
// This format is better for streaming and processing large documents
const jsonlLines = [];

// Add the main document record
jsonlLines.push(JSON.stringify({
    fileName: allData.fileName,
    client: allData.client,
    category: allData.category,
    uploadedAt: allData.uploadedAt || new Date().toISOString(),
    fileSize: allData.fileSize,
    mimeType: allData.mimeType,
    documentId: allData.documentId || allData.id,
    originalPath: allData.originalPath,
    convertedPath: allData.convertedPath,
    type: "document_metadata"
}));

// Add the content as separate line(s) - can be chunked if needed
const content = allData.convertedContent || allData.content || "";
if (content) {
    // If content is very large, you could split it into chunks here
    jsonlLines.push(JSON.stringify({
        documentId: allData.documentId || allData.id,
        content: content,
        type: "document_content"
    }));
}

// Add index data as a separate line
if (allData.indexDocument) {
    jsonlLines.push(JSON.stringify({
        documentId: allData.documentId || allData.id,
        indexData: allData.indexDocument,
        type: "index_payload"
    }));
}

// Join all lines with newline characters for JSONL format
const jsonlString = jsonlLines.join('\n');
const jsonlBuffer = Buffer.from(jsonlString);

// Create safe file name with .jsonl extension
let outputFileName = "converted.jsonl";
if (allData.safeName) {
    outputFileName = allData.safeName.replace(/\.(pdf|docx|xlsx|doc|xls)$/i, '.jsonl');
} else if (allData.fileName) {
    outputFileName = allData.fileName.replace(/[^a-zA-Z0-9.-]/g, '_').replace(/\.(pdf|docx|xlsx|doc|xls)$/i, '.jsonl');
}

return {
    json: {
        ...allData,
        uploadSuccess: true,
        indexSuccess: true,
        convertedFileName: outputFileName,
        jsonlLineCount: jsonlLines.length
    },
    binary: {
        converted: {
            data: jsonlBuffer,
            mimeType: 'application/x-ndjson',  // MIME type for JSONL
            fileName: outputFileName,
            fileExtension: 'jsonl'
        }
    }
};

// Get the original data from Prepare File Data node
const originalData = $node["Prepare File Data"].json;

// Pass through all the required data
return {
  json: {
    originalBlobUrl: originalData.originalBlobUrl,
    fileName: originalData.fileName,
    client: originalData.client,
    category: originalData.category,
    // Include all other fields
    ...originalData
  }
};
