// Extract form data
const formData = $input.first().json.body;
const clientId = formData.client;
const category = formData.category;
const action = formData.action || 'upload'; // Check for reindex action
const fileName = $input.first().binary.data.fileName;
const fileData = $input.first().binary.data;

// Azure Storage configuration
const storageAccount = 'saxtechfcs';
const rawContainer = 'fcs-clients';
const convertedContainer = 'fcs-convertedclients';
const sasTokenRaw = 'sp=racwdl&st=2025-08-08T05:00:57Z&se=2030-08-08T13:15:57Z&spr=https&sv=2024-11-04&sr=c&sig=lJKK9jDZ59pJSNkKSgwaQIrCdBaJzx4XPzgEB2%2FrnIg%3D';
const sasTokenConverted = 'sp=racwdl&st=2025-08-08T05:03:40Z&se=2030-08-08T13:18:40Z&spr=https&sv=2024-11-04&sr=c&sig=IGFgvm%2FEh6RX3Qe5ZOubR4yc6uovwicdQAufdkrQaWs%3D';

// Construct paths
const rawBlobPath = `FCS-OriginalClients/${clientId}/${category}/${fileName}`;
const uploadUrl = `https://${storageAccount}.blob.core.windows.net/${rawContainer}/${rawBlobPath}?${sasTokenRaw}`;

return {
  json: {
    clientId,
    category,
    fileName,
    rawBlobPath,
    uploadUrl,
    convertedContainer,
    sasTokenConverted,
    action,
    timestamp: new Date().toISOString()
  },
  binary: {
    data: fileData
  }
};

// Get conversion result and upload data
const conversionResult = $input.first().json;
const uploadData = $('prepare-upload').first().json;

// Extract converted content
const convertedContent = conversionResult.convertedContent || conversionResult.text || conversionResult.content || '';

// Prepare paths for converted container
const textFileName = uploadData.fileName.replace(/\.[^.]+$/, '.txt');
const convertedPath = `${uploadData.clientId}/converted/${uploadData.category}/${textFileName}`;
const metadataPath = `${uploadData.clientId}/metadata/${uploadData.category}/${uploadData.fileName}.json`;

// Prepare metadata
const metadata = {
  originalFile: uploadData.fileName,
  category: uploadData.category,
  client: uploadData.clientId,
  processedAt: new Date().toISOString(),
  conversionMethod: conversionResult.conversionMethod || 'Document Intelligence',
  originalPath: uploadData.rawBlobPath,
  convertedPath: convertedPath,
  fileSize: conversionResult.convertedSize || convertedContent.length,
  contentType: 'text/plain'
};

const storageAccount = 'saxtechfcs';
const convertedUploadUrl = `https://${storageAccount}.blob.core.windows.net/${uploadData.convertedContainer}/${convertedPath}?${uploadData.sasTokenConverted}`;
const metadataUploadUrl = `https://${storageAccount}.blob.core.windows.net/${uploadData.convertedContainer}/${metadataPath}?${uploadData.sasTokenConverted}`;

return [
  {
    json: {
      type: 'converted',
      uploadUrl: convertedUploadUrl,
      content: convertedContent,
      fileName: textFileName,
      path: convertedPath
    }
  },
  {
    json: {
      type: 'metadata',
      uploadUrl: metadataUploadUrl,
      content: JSON.stringify(metadata, null, 2),
      fileName: uploadData.fileName + '.json',
      path: metadataPath
    }
  }
];

// Prepare response
const uploadData = $('prepare-upload').first().json;
const conversionResult = $('convert-doc').first().json;

return [{
  json: {
    success: true,
    message: 'Document processed successfully',
    fileName: uploadData.fileName,
    client: uploadData.clientId,
    category: uploadData.category,
    timestamp: uploadData.timestamp,
    processing: {
      rawStoragePath: uploadData.rawBlobPath,
      convertedStoragePath: `${uploadData.clientId}/converted/${uploadData.category}/${uploadData.fileName.replace(/\.[^.]+$/, '.txt')}`,
      metadataPath: `${uploadData.clientId}/metadata/${uploadData.category}/${uploadData.fileName}.json`,
      conversionMethod: conversionResult.conversionMethod || 'Document Intelligence'
    }
  }
}];
